import React from 'react';

class Form extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            members: []
        };
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event){
        this.setState((prev) => {
            return ({
                members: [event.target.value]
            })
        })
        console.log(this.state.members)
    }

    render(){
        return (
            <div>
                <form>
                    <label>
                        First name:
                        <input type="text" name="firstName" value='' onChange={this.handleChange} />
                    </label>
                    <label>
                        Last name:
                        <input type="text" name="lastName" value='' onChange={this.handleChange} />
                    </label>
                    <label>
                        Email:
                        <input type="email" name="email" value='' onChange={this.handleChange} />
                    </label>
                    <label>
                        Event date:
                        <input type="date" name="eventDate" value='' onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }

}

export default Form;
