import React from 'react';
import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';

class EventForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            firstName: '',
            lastName: '',
            email: '',
            date: Number
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event){
        this.setState({ 
            [event.target.name] : event.target.value
        })
    }

    handleSubmit(event) {
        console.log(this.state)
        fetch('http://localhost:9000/react', 
        {
            method: 'POST',
            headers:{
                'Accept': 'application/json',
                "Content-Type": "application/json"
            },
            body: JSON.stringify(this.state)
        }).then(function(response) {
            console.log(response)
            return response.json();
        });
        event.preventDefault();
    }

    render(){
        return (
            <div>
                <h1>Brainhub Event</h1>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group >
                        <Form.Label>First Name:</Form.Label>
                        <Form.Control type="text" name="firstName" placeholder="Enter first name" onChange={this.handleChange} required />
                    </Form.Group>

                    <Form.Group >
                        <Form.Label htmlFor='lastName'>Last Name:</Form.Label>
                        <Form.Control id='lastName' type="text" name="lastName" placeholder="Enter lirst name" onChange={this.handleChange} required/>
                    </Form.Group>

                    <Form.Group>
                        <Form.Label htmlFor='email'>Email:</Form.Label>
                        <Form.Control id='email' type="email" name="email" placeholder="Email address" onChange={this.handleChange} required/>
                    </Form.Group>

                    <Form.Group >
                        <Form.Label>Date: </Form.Label>
                        <Form.Control type="date" name="date" onChange={this.handleChange} />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>

            </div>
        )
    }

}

export default EventForm;
